<template>
  <div>
    <div style="padding-top:46px">
      <NavBar>
        <div slot="left-screen" @click="onReturn">
          <img src="../../assets/return.png" />
          <span style="position:relative;top:-3px;">返回</span>
        </div>
        <div slot="title-screen">添加语料</div>
        <div slot="right-screen"></div>
      </NavBar>
      <div class="message">
        <div class="top">
          选自《{{data.title}}》
          <span>类别：{{data.type}}</span>
        </div>
        <div class="content">{{data.content}}</div>
      </div>
      <div class="control">
        <div class="container">
          <div class="recordbutton" @click="recordchance">
            <img :src=" recording? active : normal" />
          </div>
          <div class="x-loader" v-show="recording"></div>
        </div>
        <div class="f_text">
          点击话筒开始录音
          <br />
          <span v-show="this.try" @click="tryPlay">
            试听
            <audio ref="audio" autoplay src></audio>
          </span>
        </div>
      </div>

      <div style="padding-bottom:50px;box-sizing: border-box;">
        <van-cell-group>
          <van-field
            readonly
            clickable
            label="地区"
            :value="placecity"
            label-align="left"
            placeholder="选择地区"
            @click="showPlace = true"
          />
          <van-popup v-model="showPlace" position="bottom" :style="{ height: '40%' }">
            <van-area :area-list="areaList" :columns-num="2" :title="biaoti" @confirm="onPlace" />
          </van-popup>

          <van-field
            readonly
            clickable
            label="语种"
            :value="lan"
            label-align="left"
            placeholder="选择方言类别"
            @click="showPicker = true"
          />
          <van-popup v-model="showPicker" position="bottom">
            <van-picker
              show-toolbar
              :columns="columns"
              @cancel="showPicker = false"
              @confirm="onConfirm"
            />
          </van-popup>
        </van-cell-group>

        <van-button
          :disabled="disabled"
          type="info"
          size="normal"
          ref="submitbutton"
          @click="submit"
          :text="textbutton"
        />
      </div>
    </div>
    <div></div>
  </div>
</template>

<script>
import NavBar from "@/components/base/NavBar.vue";

var Record = require("../../../static/js/record.js");
import Area from "../../../static/js/area.js";

export default {
  data() {
    return {
      try: false,
      recording: false,
      areaList: Area,
      showPlace: false,
      place: null,
      city: null,
      placecity: null,
      normal: require("../../assets/hua.svg"),
      active: require("../../assets/hua1.svg"),
      textbutton: "录音并完成选择方可提交",
      data: {
        // id: 1,
        // title: "李延年歌",
        // content:
        //   "北方有佳人，绝世而独立。一顾倾人城，再顾倾人国。宁不知倾城与倾国，佳人再难得。",
        // num: 36,
        // time: "1小时前",
        // type: "诗词"
      },
      lan: null,
      showPicker: false,
      columns: ["家乡话", "家乡普通话", "普通话"],
      blobb:
        "data:audio/mpeg;base64,"
    };
  },
  components: {
    NavBar,
    Record,
    Area
  },
  methods: {
    onReturn() {
      window.history.length > 1 ? this.$router.go(-1) : this.$router.push("/");
    },
    onConfirm(value) {
      this.lan = value;
      this.showPicker = false;
    },
    
    convertBase64ToBlob(base64) {
      var base64Arr = base64.split(",");
      var imgtype = "";
      var base64String = "";
      if (base64Arr.length > 1) {
        //如果是图片base64，去掉头信息
        base64String = base64Arr[1];
        imgtype = base64Arr[0].substring(
          base64Arr[0].indexOf(":") + 1,
          base64Arr[0].indexOf(";")
        );
      }
      // 将base64解码
      var bytes = atob(base64String);
      //var bytes = base64;
      var bytesCode = new ArrayBuffer(bytes.length);
      // 转换为类型化数组
      var byteArray = new Uint8Array(bytesCode);

      // 将base64转换为ascii码
      for (var i = 0; i < bytes.length; i++) {
        byteArray[i] = bytes.charCodeAt(i);
      }
      // 生成Blob对象（文件对象）
      return new Blob([bytesCode], { type: imgtype });
    },
    submit() {
      var blobb = Record.recorder.getBlob(); 
      //  var blobbb = this.convertBase64ToBlob(
      //    ""
      //  );
// var blobbb=  new Blob([""], { type: 'audio/mp3' });
      var data = new FormData();
      data.append("dub_id", this.$route.params.id);
      data.append("record", blobbb);
      data.append("place", this.place);
      data.append("city", this.city);
      data.append("lan", this.lan);
      data.append("placecity", this.placecity);
      data.append("id", JSON.parse(localStorage.getItem("user")).id);
      console.log(data.get("record"));
      this.axios
        .post("/addDub", data)
        .then(res => {
          console.log(res);
          this.$toast.success("提交成功");
          this.$router.push(-1);
        })
        .catch(error => {
          console.log(error);
        });
    },
    onPlace(arr) {
      this.showPlace = false;
      this.place = arr[0].name;
      this.city = arr[1].name;
      if (this.place !== this.city && this.place != null) {
        this.placecity = this.place + "" + this.city;
      } else {
        this.placecity = this.place;
      }
    },
    recordchance() {
      if (this.recording == true) {
        this.stopRecording();
      } else {
        this.startRecording();
      }
      this.recording = !this.recording;
    },
    startRecording() {
      Record.HZRecorder.get(function(rec) {
        Record.recorder = rec;
        Record.recorder.start(); //录音开始
        console.log("录音开始");
      });
    },
    stopRecording() {
      Record.recorder.stop(); //录音结束
      this.$toast.success({ duration: 500, message: "录音完成" });
      this.try = true;
    },
    playRecording() {
      Record.recorder.play(this.$refs.audio); //试听开始
    },
    tryPlay() {
      Record.recorder.play(this.$refs.audio);
      console.log(this.$refs.audio.src);
    },
    //根据id获取语料详情
    getDubDetails() {
      var data = new FormData();
      data.append("id", this.$route.params.id);
      this.axios
        .post("/dub/id", data)
        .then(res => {
          console.log(res);
          this.data = res.data.data;
        })
        .catch(error => {
          console.log(error);
        });
    }
  },
  computed: {
    disabled: function() {
      if (this.lan && this.placecity) {
        this.textbutton = "提交";
      }
      return this.lan && this.placecity ? false : true;
    }
  },
  mounted() {
    this.getDubDetails();
  },
   deactivated() {
    //销毁实例，防止缓存
    this.$destroy(true);
  }
};
</script>

